package programming_Challenge70_76;

public class Concat_uppercase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "Hello ";
		String str2 = "welcome to uppercase program";
		String str= str1.concat(str2);
		System.out.println("The concatenated string is: "+str);
		
		System.out.println("The convert Case String is: "+ str.toUpperCase());

	}

}
